import React, { useState } from 'react';
import './LandingPage.css';
import CustomNavbar from '../components/Navbar';

const LandingPage = () => {
  const [playAudio, setPlayAudio] = useState(false);

  const handlePlayAudio = () => {
    setPlayAudio(true);
  };

  return (
    <div className="landing-page">
      <CustomNavbar />
      <div className="background"></div>
      <div className="logo-container">
        <img src="/images/eventme-logo.jpg" alt="EventMe Logo" className="landing-logo" />
        <h1>Welcome to EventMe</h1>
      </div>
      {!playAudio && (
        <button onClick={handlePlayAudio} className="play-audio-button">
          Play Audio
        </button>
      )}
      {playAudio && (
        <iframe
          className="audio-iframe"
          src="https://www.youtube.com/embed/jfKfPfyJRdk?autoplay=1&mute=0"
          title="YouTube video player"
          frameBorder="0"
          allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
          allowFullScreen
        ></iframe>
      )}
    </div>
  );
};

export default LandingPage;
