import React, { useContext, useEffect } from 'react';
import CustomNavbar from '../components/Navbar';
import { AudioContext } from '../context/AudioContext';
import charlotteSkyline from '../images/charlotte_skyline.jpg'; // Import the image

const LandingPage = () => {
  const { togglePlayPause } = useContext(AudioContext);

  useEffect(() => {
    togglePlayPause();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const landingPageStyle = {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    textAlign: 'center',
    padding: '20px',
    height: '100vh',
    background: `url(${charlotteSkyline}) no-repeat center center/cover`,
    color: '#fff',
  };

  return (
    <div style={landingPageStyle}>
      <CustomNavbar />
      <div className="logo-container">
        <img src="/images/eventme-logo.jpg" alt="EventMe Logo" className="landing-logo" />
        <h1>Welcome to EventMe</h1>
      </div>
    </div>
  );
};

export default LandingPage;
